mapper.select:
SELECT REVIEW_NUMBER, REVIEW_TITLE, REVIEW_CONTENT, REVIEW_SYSDATE, REVIEW_RATE, USER_NUMBER, PRODUCT_NUMBER, USER_ID, REVIEW_IMG_NUMBER, REVIEW_IMG_NAME, REVIEW_IMG_UPLOAD_PATH, REVIEW_IMG_UUID FROM ( SELECT ROWNUM RNUM, REVIEW_NUMBER, REVIEW_TITLE, REVIEW_CONTENT, REVIEW_SYSDATE, REVIEW_RATE, USER_NUMBER, PRODUCT_NUMBER, USER_ID, REVIEW_IMG_NUMBER, REVIEW_IMG_NAME, REVIEW_IMG_UPLOAD_PATH, REVIEW_IMG_UUID FROM ( select R.REVIEW_NUMBER, REVIEW_TITLE, REVIEW_CONTENT, REVIEW_SYSDATE, REVIEW_RATE, R.USER_NUMBER, PRODUCT_NUMBER, U.USER_ID, I.REVIEW_IMG_NUMBER, REVIEW_IMG_NAME, REVIEW_IMG_UPLOAD_PATH, REVIEW_IMG_UUID from TBL_SHOP_REVIEW R left join TBL_USER U on R.USER_NUMBER = U.USER_NUMBER left join TBL_REVIEW_IMG I on R.REVIEW_NUMBER = I.REVIEW_NUMBER where PRODUCT_NUMBER=#{productNumber} )
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

select COUNT(REVIEW_NUMBER)
from TBL_SHOP_REVIEW
where PRODUCT_NUMBER=#{productNumber}

select NVL(round(avg(REVIEW_RATE),1), 0) as starAvg
from TBL_SHOP_REVIEW
where PRODUCT_NUMBER=#{productNumber}

select PRODUCT_NUMBER
from TBL_PRODUCT
where PRODUCT_NUMBER=#{productNumber}
